%sh

echo Standorte:
curl -s 'https://data.stadt-zuerich.ch/dataset/ugz_luftschadstoffmessung_stundenwerte/download/uzg_ogd_metadaten.json' | jq '.Standorte[] | {Name: .Name, Adresse: .Adresse, Beschreibung: .Beschreibung }'

echo Parameter:
curl -s 'https://data.stadt-zuerich.ch/dataset/ugz_luftschadstoffmessung_stundenwerte/download/uzg_ogd_metadaten.json' | jq '.Parameter[0:8]'

echo Grenzwerte:
curl -s 'https://data.stadt-zuerich.ch/dataset/ugz_luftschadstoffmessung_stundenwerte/download/uzg_ogd_metadaten.json' | jq '.Grenzwerte[] | {Schadstoff: .ID, Immisionsgrenzwert: .Immisionsgrenzwert, Einheit: .Einheit, "Statistische Definition": ."Statistische Definition"}'

